sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	var dept = g_form.getValue('department');
	if(dept == 'cse'){
		g_form.setValue('hod_name','Abel Tuter');
	}
	else if(dept == 'ece'){
		g_form.setValue('hod_name','Abraham Lincoln');
	}
	else if(dept == 'mech'){
		g_form.setValue('hod_name','Adela Cervantsz');
	}
	else if(dept == 'eee'){
		g_form.setValue('hod_name','Aileen Mottern');
	}
	else if(dept == 'civil'){
		g_form.setValue('hod_name','Alejandro Mascall');
	}


}
